import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [currentView, setCurrentView] = useState("home");
  const [aiReaders, setAiReaders] = useState([]);
  const [tarotCards, setTarotCards] = useState({});
  const [readingTypes, setReadingTypes] = useState({});
  const [coinPackages, setCoinPackages] = useState([]);
  const [userBalance, setUserBalance] = useState(10); // Start with 10 free coins
  const [selectedReader, setSelectedReader] = useState(null);
  const [selectedReadingType, setSelectedReadingType] = useState(null);
  const [question, setQuestion] = useState("");
  const [currentReading, setCurrentReading] = useState(null);
  const [readingHistory, setReadingHistory] = useState([]);
  const [currentQuote, setCurrentQuote] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredCards, setFilteredCards] = useState([]);
  const [quizQuestions, setQuizQuestions] = useState([]);
  const [currentQuiz, setCurrentQuiz] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [userId] = useState("user_" + Math.random().toString(36).substr(2, 9));

  const API_BASE = "http://127.0.0.1:5000/api";

  useEffect(() => {
    // Load initial data
    fetchData();
    fetchRandomQuote();

    // Set up quote rotation
    const quoteInterval = setInterval(fetchRandomQuote, 30000); // Every 30 seconds

    return () => clearInterval(quoteInterval);
  }, []);

  useEffect(() => {
    // Filter cards based on search query
    if (searchQuery.trim() === "") {
      setFilteredCards(Object.values(tarotCards).slice(0, 20));
    } else {
      const filtered = Object.values(tarotCards).filter(card =>
        card.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        card.upright.keywords.some(keyword => 
          keyword.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
      setFilteredCards(filtered);
    }
  }, [searchQuery, tarotCards]);

  const fetchData = async () => {
    try {
      const [readersRes, cardsRes, typesRes, packagesRes] = await Promise.all([
        fetch(`${API_BASE}/readers`),
        fetch(`${API_BASE}/cards`),
        fetch(`${API_BASE}/reading-types`),
        fetch(`${API_BASE}/coin-packages`)
      ]);

      const [readers, cards, types, packages] = await Promise.all([
        readersRes.json(),
        cardsRes.json(),
        typesRes.json(),
        packagesRes.json()
      ]);

      setAiReaders(readers);
      setTarotCards(cards);
      setReadingTypes(types);
      setCoinPackages(packages);
      setFilteredCards(Object.values(cards).slice(0, 20));
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const fetchRandomQuote = async () => {
    try {
      const response = await fetch(`${API_BASE}/quote`);
      const data = await response.json();
      setCurrentQuote(data.quote);
    } catch (error) {
      console.error("Error fetching quote:", error);
    }
  };

  const performReading = async () => {
    if (!selectedReader || !selectedReadingType) {
      alert("Please select a reader and reading type");
      return;
    }

    try {
      const response = await fetch(`${API_BASE}/reading`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          reader_id: selectedReader.id,
          reading_type: selectedReadingType,
          question: question,
          user_id: userId
        }),
      });

      if (response.ok) {
        const reading = await response.json();
        setCurrentReading(reading);
        setUserBalance(prev => prev - reading.cost);
        setCurrentView("reading-result");
      } else {
        const error = await response.json();
        alert(error.error || "Error performing reading");
      }
    } catch (error) {
      console.error("Error performing reading:", error);
      alert("Error performing reading");
    }
  };

  const purchaseCoins = async (packageId) => {
    try {
      const response = await fetch(`${API_BASE}/user/${userId}/purchase`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ package_id: packageId }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserBalance(data.new_balance);
        alert(`Successfully purchased ${data.coins_added} coins!`);
      }
    } catch (error) {
      console.error("Error purchasing coins:", error);
      alert("Error purchasing coins");
    }
  };

  const startQuiz = async () => {
    try {
      const response = await fetch(`${API_BASE}/quiz/questions`);
      const questions = await response.json();
      setQuizQuestions(questions);
      setCurrentQuiz(questions.slice(0, 5)); // Take first 5 questions
      setCurrentView("quiz");
    } catch (error) {
      console.error("Error starting quiz:", error);
    }
  };

  const submitQuiz = async () => {
    const answers = Object.keys(quizAnswers).map(questionId => ({
      questionId: parseInt(questionId),
      answer: quizAnswers[questionId],
      correct: quizQuestions.find(q => q.id === parseInt(questionId))?.correct === quizAnswers[questionId]
    }));

    try {
      const response = await fetch(`${API_BASE}/quiz/submit`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          answers: answers,
          user_id: userId
        }),
      });

      if (response.ok) {
        const result = await response.json();
        setUserBalance(result.new_balance);
        alert(`Quiz completed! Score: ${result.score}/${result.total} (${result.percentage.toFixed(1)}%). You earned ${result.coins_earned} coins!`);
        setCurrentView("home");
        setCurrentQuiz(null);
        setQuizAnswers({});
      }
    } catch (error) {
      console.error("Error submitting quiz:", error);
    }
  };

  const renderHome = () => (
    <div className="home-container">
      <div className="hero-section">
        <h1 className="main-title">🔮 AI Tarot</h1>
        <p className="subtitle">Where Ancient Wisdom Meets Modern Technology</p>
        <div className="quote-display">
          <p className="mystical-quote">"{currentQuote}"</p>
        </div>
      </div>

      <div className="balance-display">
        <span className="coin-icon">🪙</span>
        <span className="balance-text">{userBalance} Coins</span>
      </div>

      <div className="ai-readers-section">
        <h2>🌟 Choose Your AI Reader</h2>
        <div className="readers-grid">
          {aiReaders.map(reader => (
            <div 
              key={reader.id} 
              className={`reader-card ${selectedReader?.id === reader.id ? 'selected' : ''}`}
              onClick={() => setSelectedReader(reader)}
            >
              <div className="reader-icon">{reader.icon}</div>
              <h3>{reader.name}</h3>
              <p className="reader-specialty">{reader.specialty}</p>
              <p className="reader-description">{reader.description}</p>
              <div className="reader-accuracy">Accuracy: {reader.accuracy}</div>
            </div>
          ))}
        </div>
      </div>

      <div className="reading-types-section">
        <h2>🔮 Choose Your Reading</h2>
        <div className="reading-types-grid">
          {Object.entries(readingTypes).map(([key, type]) => (
            <div 
              key={key}
              className={`reading-type-card ${selectedReadingType === key ? 'selected' : ''}`}
              onClick={() => setSelectedReadingType(key)}
            >
              <h3>{type.name}</h3>
              <p>{type.description}</p>
              <div className="reading-cost">Cost: {type.cost} 🪙</div>
            </div>
          ))}
        </div>
      </div>

      {selectedReader && selectedReadingType && (
        <div className="question-section">
          <h2>❓ Ask Your Question</h2>
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="What would you like to know? (optional)"
            className="question-input"
          />
          <button 
            onClick={performReading}
            className="perform-reading-btn"
            disabled={userBalance < readingTypes[selectedReadingType].cost}
          >
            🔮 Get Reading ({readingTypes[selectedReadingType].cost} 🪙)
          </button>
        </div>
      )}

      <div className="navigation-buttons">
        <button onClick={() => setCurrentView("library")} className="nav-btn">
          📚 Card Library
        </button>
        <button onClick={() => setCurrentView("shop")} className="nav-btn">
          🛒 Buy Coins
        </button>
        <button onClick={startQuiz} className="nav-btn">
          🧠 Take Quiz
        </button>
        <button onClick={() => setCurrentView("history")} className="nav-btn">
          📜 Reading History
        </button>
      </div>
    </div>
  );

  const renderReadingResult = () => (
    <div className="reading-result-container">
      <button onClick={() => setCurrentView("home")} className="back-btn">
        ← Back to Home
      </button>

      {currentReading && (
        <div className="reading-content">
          <div className="reader-info">
            <span className="reader-icon">{currentReading.reader.icon}</span>
            <h2>{currentReading.reader.name}</h2>
            <p className="reading-question">Question: {currentReading.question || "General reading"}</p>
          </div>

          <div className="cards-display">
            <h3>Cards Drawn:</h3>
            <div className="cards-grid">
              {currentReading.cards.map((cardData, index) => (
                <div key={index} className={`card-display ${cardData.reversed ? 'reversed' : ''}`}>
                  <div className="card-image">{cardData.card.image}</div>
                  <h4>{cardData.card.name}</h4>
                  <p className="card-position">Position {cardData.position}</p>
                  <p className="card-orientation">
                    {cardData.reversed ? '🔄 Reversed' : '⬆️ Upright'}
                  </p>
                </div>
              ))}
            </div>
          </div>

          <div className="interpretation">
            <h3>✨ Interpretation</h3>
            <div className="interpretation-summary">
              <p>{currentReading.interpretation.summary}</p>
            </div>

            <div className="card-interpretations">
              {currentReading.interpretation.cards.map((interpretation, index) => (
                <div key={index} className="card-interpretation">
                  <h4>Position {interpretation.position}: {interpretation.card_name}</h4>
                  <p>{interpretation.interpretation}</p>
                  <div className="keywords">
                    Keywords: {interpretation.keywords.join(', ')}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );

  const renderLibrary = () => (
    <div className="library-container">
      <button onClick={() => setCurrentView("home")} className="back-btn">
        ← Back to Home
      </button>

      <h2>📚 Tarot Card Library</h2>

      <div className="search-section">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search cards by name or keywords..."
          className="search-input"
        />
      </div>

      <div className="cards-library">
        {filteredCards.map((card, index) => (
          <div key={index} className="library-card">
            <div className="card-header">
              <span className="card-image">{card.image}</span>
              <h3>{card.name}</h3>
              <span className="card-suit">{card.suit}</span>
            </div>

            <div className="card-meanings">
              <div className="upright-meaning">
                <h4>⬆️ Upright</h4>
                <p>{card.upright.description}</p>
                <div className="keywords">
                  Keywords: {card.upright.keywords.join(', ')}
                </div>
              </div>

              <div className="reversed-meaning">
                <h4>🔄 Reversed</h4>
                <p>{card.reversed.description}</p>
                <div className="keywords">
                  Keywords: {card.reversed.keywords.join(', ')}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderShop = () => (
    <div className="shop-container">
      <button onClick={() => setCurrentView("home")} className="back-btn">
        ← Back to Home
      </button>

      <h2>🛒 Purchase Coins</h2>
      <p className="shop-description">Expand your mystical journey with more readings</p>

      <div className="coin-packages">
        {coinPackages.map(coinPackage => (
          <div key={coinPackage.id} className="coin-package">
            <div className="package-header">
              <span className="coin-icon">🪙</span>
              <h3>{coinPackage.coins} Coins</h3>
              {coinPackage.bonus > 0 && (
                <span className="bonus-badge">+{coinPackage.bonus} Bonus!</span>
              )}
            </div>
            <div className="package-price">₹{coinPackage.price}</div>
            <button 
              onClick={() => purchaseCoins(coinPackage.id)}
              className="purchase-btn"
            >
              Purchase
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  const renderQuiz = () => (
    <div className="quiz-container">
      <button onClick={() => setCurrentView("home")} className="back-btn">
        ← Back to Home
      </button>

      <h2>🧠 Tarot Knowledge Quiz</h2>
      <p className="quiz-description">Test your knowledge and earn coins!</p>

      {currentQuiz && (
        <div className="quiz-content">
          {currentQuiz.map((question, index) => (
            <div key={question.id} className="quiz-question">
              <h3>Question {index + 1}: {question.question}</h3>
              <div className="quiz-options">
                {question.options.map((option, optionIndex) => (
                  <label key={optionIndex} className="quiz-option">
                    <input
                      type="radio"
                      name={`question-${question.id}`}
                      value={optionIndex}
                      onChange={(e) => setQuizAnswers({
                        ...quizAnswers,
                        [question.id]: parseInt(e.target.value)
                      })}
                    />
                    <span>{option}</span>
                  </label>
                ))}
              </div>
            </div>
          ))}

          <button 
            onClick={submitQuiz}
            className="submit-quiz-btn"
            disabled={Object.keys(quizAnswers).length < currentQuiz.length}
          >
            Submit Quiz
          </button>
        </div>
      )}
    </div>
  );

  const renderHistory = () => (
    <div className="history-container">
      <button onClick={() => setCurrentView("home")} className="back-btn">
        ← Back to Home
      </button>

      <h2>📜 Reading History</h2>

      {readingHistory.length === 0 ? (
        <p className="no-history">No readings yet. Start your mystical journey!</p>
      ) : (
        <div className="history-list">
          {readingHistory.map((reading, index) => (
            <div key={index} className="history-item">
              <div className="history-header">
                <span className="reader-icon">{reading.reader.icon}</span>
                <h3>{reading.reader.name}</h3>
                <span className="reading-date">
                  {new Date(reading.timestamp).toLocaleDateString()}
                </span>
              </div>
              <p className="reading-question">Question: {reading.question || "General reading"}</p>
              <div className="reading-cards">
                Cards: {reading.cards.map(card => card.card.name).join(', ')}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className="app">
      <div className="app-background">
        {currentView === "home" && renderHome()}
        {currentView === "reading-result" && renderReadingResult()}
        {currentView === "library" && renderLibrary()}
        {currentView === "shop" && renderShop()}
        {currentView === "quiz" && renderQuiz()}
        {currentView === "history" && renderHistory()}
      </div>
    </div>
  );
}

export default App;
